(function($){$.notifly=function(options){var settings={"header":"","content":"","delay":2000,"inout":500}
if(options){$.extend(settings,options);}
$("body").append('<div class="notifly">'+'<div class="notiflyHeader"></div>'+'<div class="notiflyContent"></div>'+'</div>');$(".notifly").addClass("corner");$(".notiflyHeader").html(settings.header);$(".notiflyContent").html(settings.content);$(".notifly").fadeIn(settings.inout);$(".notifly").delay(settings.delay);$(".notifly").fadeOut(settings.inout,function(){$(".notifly").detach();});};})(jQuery);